	int total;

	public int totalNQueens(int n) {
		if (n <= 0) {
			return 0;
		}
		helper(new int[n], n, 0);
		return total;
    }

	private void helper(int[] path, int n, int pos) {
		if (pos >= n) {
			total++;
			return;
		}
		for (int i = 0; i < n; i++) {
			path[pos] = i;
			if (!isValid(path, pos)) {
				continue;
			}
			helper(path, n, pos + 1);
		}
	}

	private boolean isValid(int[] path, int pos) {
		for (int i = 0; i < pos; i++) {
			// check path[i] and path[pos]
			if (path[i] == path[pos]) {
				return false;
			}
			if (path[i] - path[pos] == pos - i) {
				return false;
			}
			if (path[pos] - path[i] == pos - i) {
				return false;
			}
		}
		return true;
	}

__Next Permutation__

    public void nextPermutation(int[] num) {
        if (num == null || num.length <= 1) {
			return;
		}
		int len = num.length;
		int p = len - 2;
		while (p >= 0 && num[p] >= num[p + 1]) {
			p--;
		}
		if (p < 0) {
			Arrays.sort(num);
		} else {
			int k = len - 1;
			while (k >= 0 && num[k] <= num[p]) {
				k--;
			}
			swap(num, p, k);
			reverse(num, p + 1, len - 1);
		}
    }

	private void swap(int[] num, int p, int k) {
		num[p] = num[p] + num[k];
		num[k] = num[p] - num[k];
		num[p] = num[p] - num[k];
	}

	private void reverse(int[] num, int s, int d) {
		while (s < d) {
			swap(num, s++, d--);
		}
	}

