__Triangle__

    public int minimumTotal(List<List<Integer>> triangle) {
        if (triangle == null || triangle.size() == 0) {
            return 0;
        }
        int len = triangle.size();
        int[][] dp = new int[len][len];
        for (int i = len - 1; i >= 0; i--) {
            // bottom-up approach (row by row)
            for (int j = 0; j <= i; j++) {
                if (i == len - 1) {
                    dp[i][j] = triangle.get(i).get(j);
                } else {
                    dp[i][j] = triangle.get(i).get(j)
                            + Math.min(dp[i+1][j], dp[i+1][j+1]);
                }
            }
        }
        return dp[0][0];
    }

__Unique Paths__

    public int uniquePaths(int m, int n) {
        if (m == 0 && n == 0) {
            return 0;
        }
        int[][] dp = new int[m][n];
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (i == 0 || j == 0) {
                    dp[i][j] = 1;
                } else {
                    dp[i][j] = dp[i-1][j] + dp[i][j-1];
                }
            }
        }
        return dp[m-1][n-1];
    }

__Unique Paths II__

    public int uniquePathsWithObstacles(int[][] obstacleGrid) {
        if (obstacleGrid == null) {
            return 0;
        }
        int m = obstacleGrid.length;
        int n = obstacleGrid[0].length;
        int[][] dp = new int[m][n];
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (obstacleGrid[i][j] != 0) {
                    dp[i][j] = 0;
                } else if (i == 0 && j == 0) {
                    dp[i][j] = 1;
                } else if (i == 0) {
                    dp[i][j] = dp[i][j-1];
                } else if (j == 0) {
                    dp[i][j] = dp[i-1][j];
                } else {
                    dp[i][j] = dp[i-1][j] + dp[i][j-1];
                }
            }
        }
        return dp[m-1][n-1];
    }

__Minimum Path Sum__

    public int minPathSum(int[][] grid) {
        if (grid == null) {
            return 0;
        }
        int m = grid.length;
        int n = grid[0].length;
        int[][] dp = new int[m][n];
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (i == 0 && j == 0) {
                    dp[i][j] = grid[i][j];
                } else if (i == 0) {
                    dp[i][j] = dp[i][j-1] + grid[i][j];
                } else if (j == 0) {
                    dp[i][j] = dp[i-1][j] + grid[i][j];
                } else {
                    dp[i][j] = grid[i][j] + Math.min(dp[i-1][j], dp[i][j-1]);
                }
            }
        }
        return dp[m-1][n-1];
    }

#### Type 2.1: Sequence Dp

__Climbing Stairs__

    public int climbStairs(int n) {
        if (n <= 2) {
			return n;
		}
		int[] dp = new int[n];
		dp[0] = 1;
		dp[1] = 2;
		for (int i = 2; i < n; i++) {
			dp[i] = dp[i - 2] + dp[i - 1];
		}
		return dp[n - 1];
    }

__Jump Game__

    public boolean canJump(int[] A) {
        if (A == null || A.length == 0) 
			return false;
		int reach = 0;
		int len = A.length;
		for (int i = 0; i < len; i++) {
			if (i > reach) 
				break;
			reach = Math.max(reach, i + A[i]);
			if (reach >= len - 1) 	
				return true;
		}
		return false;
    }

__Jump Game II__

    public int jump(int[] A) {
        if (A == null || A.length <= 1) 
			return 0;
		int[] dp = new int[A.length];
		int cur = 1;
		for (int i = 0; i < A.length - 1; i++) {
			while (cur <= i + A[i] && cur < dp.length) {
				dp[cur] = dp[i] + 1;
				cur++;
			}
			if (cur == dp.length) {
				break;
			}
		}
		return dp[A.length - 1];
    }

__Palindrome Partitioning II__

    public int minCut(String s) {
        if (s == null || s.length() <= 1) {
            return 0;
        }
        boolean[][] map = palindromeMap(s);
        int len = s.length();
        int[] dp = new int[len + 1];
        dp[0] = -1;
        for (int i = 1; i <= len; i++) {
            dp[i] = Integer.MAX_VALUE;
            for (int j = 1; j <= i; j++) {
                if (map[i-1][j-1]) {
                    dp[i] = Math.min(dp[i], dp[j-1] + 1);
                }
            }
        }
        return dp[len];
    }

    private boolean[][] palindromeMap(String s) {
        int len = s.length();
        boolean[][] map = new boolean[len][len];
        char[] ss = s.toCharArray();
        for (int i = 0; i < len; i++) {
            for (int j = i; j >= 0; j--) {
                if (i == j) {
                    map[i][j] = true;
                } else if (i - j == 1) {
                    map[i][j] = ss[i] == ss[j];
                } else {
                    map[i][j] = (ss[i] == ss[j]) & map[i - 1][j + 1];
                }
            }
        }
        return map;
    }

__Word Break__

    public boolean wordBreak(String s, Set<String> dict) {
        if (s == null || s.length() == 0) {
            return true;
        }
        int len = s.length();
        boolean[] dp = new boolean[len + 1];
        dp[0] = true;
        for (int i = 1; i <= len; i++) {
            for (int j = 0; j < i; j++) {
                if (!dp[j]) {
                    continue;
                }
                if (dict.contains(s.substring(j, i))) {
                    dp[i] = true;
                    break;
                }
            }
        }
        return dp[len];
    }

